const {createClient} = require('soap')
const wsdl = 'http://www.webservicex.net/sunsetriseservice.asmx?WSDL'

createClient(wsdl, (err, client) => {
  if (err) {
    console.error(err)
    process.exit(1)
  } else {
    client.on('request', (xml) => console.log(xml))
    client.on('response', (xml) => console.log(xml))
    client
      .GetSunSetRiseTime({
        L: {
          Latitude: 0,
          Longitude: 0,
          SunSetTime: 0,
          SunRiseTime: 0,
          TimeZone: 0,
          Day: 0,
          Month: 0,
          Year: 0
        }
      }, (err, res) => {
        if (err) {
          console.error(err)
          process.exit(1)
        } else {
          console.log(res)
          process.exit(0)
        }
      })
  }
})
